import twitterLogo from './assets/twitter-logo.svg';
import './App.css';
import {useEffect, useState} from 'react';
import idl from './idl.json';

// Constants

// TEST GIFS
const TEST_GIFS = [
	'https://i.giphy.com/media/eIG0HfouRQJQr1wBzz/giphy.webp',
	'https://media3.giphy.com/media/L71a8LW2UrKwPaWNYM/giphy.gif?cid=ecf05e47rr9qizx2msjucl1xyvuu47d7kf25tqt2lvo024uo&rid=giphy.gif&ct=g',
	'https://media4.giphy.com/media/AeFmQjHMtEySooOc8K/giphy.gif?cid=ecf05e47qdzhdma2y3ugn32lkgi972z9mpfzocjj6z1ro4ec&rid=giphy.gif&ct=g',
	'https://i.giphy.com/media/PAqjdPkJLDsmBRSYUp/giphy.webp'
]

// buildspace twitter stuff
const TWITTER_HANDLE = '_buildspace';
const TWITTER_LINK = `https://twitter.com/${TWITTER_HANDLE}`;

const App = () => {
  // State
  const [walletAddress, setWalletAddress] = useState(null);
  const [inputValue, setInputValue] = useState('');
  const [gifList, setGifList] = useState([]);
  // Is phantom wallet connected?
  const checkIfWalletIsConnected = async () => {
    try {
      const {solana} = window;

      if(solana) {
        if(solana.isPhantom) {
          console.log('Phantom Wallet is found!');
          const response = await solana.connect({onlyIfTrusted: true});
          console.log('Connected with PubKey:', response.publicKey.toString());
          setWalletAddress(response.publicKey.toString());
        } else {
          alert('Solana obj is not found - get Phantom Wallet');
        }
      }
    } catch (err) {
      console.log('err');
    }
  };

  // Connect Wallet Logic
  const connectWallet = async () => {
    // Render UI when user wallet is not connected
    const {solana} = window;

    if(solana) {
      const response = await solana.connect();
      console.log('Connected with pubKey:', response.publicKey.toString());
      setWalletAddress(response.publicKey.toString());
    }
  };

  // Send Gif from valid link to our grid
  const sendGif = async () => {
    if(inputValue.length > 0) {
      console.log('Gif link supplied:', inputValue);
    } else {
      console.log('Empty input value - try again.');
    }
  };

  // Get Input Change
  const onInputChange = (event) => {
    const {value} = event.target;
    setInputValue(value);
  };

  const renderNotConnectedContainer = () => (
    <button
      className='cta-button connect-wallet-button'
      onClick={connectWallet}
    >
      Connect Wallet!
    </button>
  );

  const renderConnectedContainer = () => (
    <div className='connected-container'>
      <input type='text' placeholder='Enter gif link here!' value={inputValue} onChange={onInputChange}/>
      <button className='cta-button submit-gif-button' onClick={sendGif}>Submit Gif!</button>
      <div className='gif-grid'>
        {gifList.map((gif) => (
          <div className='gif-item' key={gif}>
            <img src={gif} alt={gif} />
          </div>
        ))}
      </div>
    </div>
  );

  // Check for connected wallet
  useEffect(() => {
    window.addEventListener('load', async (event) => {
      await checkIfWalletIsConnected();
    });
  }, []);

  // Fetch gif list from connected wallet
  useEffect(() => {
    if(walletAddress) {
      console.log('Fetching Gif List...');

      // Call solana program && set state
      setGifList(TEST_GIFS);
    }
  }, [walletAddress]);

  return (
    <div className="App">
      <div className={walletAddress ? 'authed-container' : 'container'}>
        <div className="header-container">
          <p className="header">ðŸ–¼ jetgoof's portal to Solana</p>
          <p className="sub-text">
            Jump into your metaverse gif library âœ¨
          </p>
          {!walletAddress && renderNotConnectedContainer()}
          {walletAddress && renderConnectedContainer()}
        </div>
        <div className="footer-container">
          <img alt="Twitter Logo" className="twitter-logo" src={twitterLogo} />
          <a
            className="footer-text"
            href={TWITTER_LINK}
            target="_blank"
            rel="noreferrer"
          >{`built on @${TWITTER_HANDLE}`}</a>
        </div>
      </div>
    </div>
  );
};

export default App;
